import React, {useState} from 'react'
import "./TicTacToe.scss"
import { RxCross1 } from 'react-icons/rx'
import { FaRegCircle } from 'react-icons/fa'
import { removeIconAction, removeVisibleAction, TypeTicTacToe } from '../../store/Reducers/Reducer'
import { useDispatch } from 'react-redux';
import { useTypedSelector } from '../../store/Reducers'


interface TypeProps {
    TicTacToe: TypeTicTacToe[],
    crossOrCircle: boolean,
    setCrossOrCircle: (crossOrCircle: boolean) => void,
    visibleIcon: (t: TypeTicTacToe, crossOrCircle: boolean) => void
}


const CrossAndCircleSpace: React.FC = () => {
    
    const [crossOrCircle, setCrossOrCircle] = useState<boolean>(false)

  const dispatch = useDispatch()
  const {TicTacToe} = useTypedSelector(state => state.ticTacToe)


  const visibleIcon = (t: TypeTicTacToe, crossOrCircle: boolean) => {
    dispatch(removeVisibleAction(t))
    dispatch(removeIconAction(t, crossOrCircle))
    setCrossOrCircle(!crossOrCircle)
  }


    return (
        <div className='container_ttt'>
            {TicTacToe.map(t => 
                <div onClick={() => visibleIcon(t, crossOrCircle)} key={t.id}>
                    {t.visible
                    ? <div className="box">
                        {t.icon
                        ? <RxCross1 className='icon'/>
                        : <FaRegCircle className='icon'/>
                        }
                      </div>
                    : <div className="box"></div>
                    }
                </div>    
            )}
        </div>
    )
}

export default CrossAndCircleSpace;
